// Pylon Spacing Variables â€” Generated by figma-sync
// Execute via Figma Console MCP (figma_execute)

const collections = await figma.variables.getLocalVariableCollectionsAsync();
let collection = collections.find(c => c.name === "Pylon/Spacing");
if (!collection) {
  collection = figma.variables.createVariableCollection("Pylon/Spacing");
}
const modeId = collection.modes[0].modeId;

async function upsertFloat(name, value) {
  const existing = (await figma.variables.getLocalVariablesAsync("FLOAT"))
    .find(v => v.name === name && v.variableCollectionId === collection.id);
  const variable = existing || figma.variables.createVariable(name, collection, "FLOAT");
  variable.setValueForMode(modeId, value);
  variable.scopes = ["GAP", "WIDTH_HEIGHT"];
  variable.setVariableCodeSyntax("WEB", "var(--pylon-space) * " + (value / 4));
}

await upsertFloat("space/0", 0);
await upsertFloat("space/1", 4);
await upsertFloat("space/2", 8);
await upsertFloat("space/3", 12);
await upsertFloat("space/4", 16);
await upsertFloat("space/5", 20);
await upsertFloat("space/6", 24);
await upsertFloat("space/8", 32);
await upsertFloat("space/10", 40);
await upsertFloat("space/12", 48);
await upsertFloat("space/16", 64);
await upsertFloat("space/20", 80);

const count = (await figma.variables.getLocalVariablesAsync("FLOAT"))
  .filter(v => v.variableCollectionId === collection.id).length;
console.log("\u2713 Pylon spacing synced: " + count + " variables");
